using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using static System.Console;

namespace tp1_partie_2
{
    class Program : NetProcessing.Sketch
    {
        static void Main(string[] args)
        {
            HideConsole();
            new Program().Start();
        }

        public override void NPMain()
        {
            //CANEVAS
            Size(1000, 1000);
            Background(0,255,0);
            //CONSTANTES
            const int FACTEUR_AGRANDISSEMENT_TERRAIN = 3;
            const int FACTEUR_AGRANDISSEMENT_BUT = 9;
            const int APEX_X = 30;   //Coordonnées en x de l'apex
            const int APEX_Y = 30;   //coordonnées en y de l'apex
            const int CÔTÉ_AVANT_CHAMP = 90 * FACTEUR_AGRANDISSEMENT_TERRAIN;
            const int LARGEUR_COULOIR_AVANT_CHAMP = 6 * FACTEUR_AGRANDISSEMENT_TERRAIN;
            const int ARC_ENTRE_CHAMP = 95 * FACTEUR_AGRANDISSEMENT_TERRAIN;
            const int RAYON_MONTICULE = 9 * FACTEUR_AGRANDISSEMENT_TERRAIN;


            //VARIABLES
            int centrePlaqueX = APEX_X + CÔTÉ_AVANT_CHAMP / 2 + LARGEUR_COULOIR_AVANT_CHAMP / 2;
            int centrePlaqueY = APEX_Y - CÔTÉ_AVANT_CHAMP / 2 - LARGEUR_COULOIR_AVANT_CHAMP / 2;
            //LOGIQUE
            Ellipse(180, 180, 285, 285);
            

        }
    }
}


